import React, {Component} from 'react'
import FieldForm from './FieldForm'

class Form extends Component {
  constructor() {
    super()


    this.handleChange = this.handleChange.bind(this)

    this.state = {
      estadoFavorito: '',
      nome: '',
    }
  }

  handleChange({target}) {
    const {name} = target
    const value = target.type === 'checkbox' ? target.checked : target.value

    this.setState({
      [name]: value
    })
  }

  render() {
    return (
      <div>
        <h1>Formulário</h1>
          <form className="form">
          <FieldForm value={this.state.estadoFavorito} handleChange={this.handleChange}/>
          <label>
            Nome
            <input name="nome" type="text" onChange={this.handleChange}/>
          </label>
        </form>
      </div>
    )
  }
}

export default Form
