class Form extends Component {
  constructor() {
    super()

    this.handleChange = this.handleChange.bind(this)

    this.state = {
      nome: '',
    }

  }

  handleChange({target}) {
    const {name, value} = target

    this.setState({
      [name]: value
    })
  }

  render() {
    return (
      <div>
        <h1>Formulário</h1>
        <form className="form">

        <label>
          Nome
