import React from "react";

class App extends React.Component {
  constructor() {
    super();

    this.fetchDogImg = this.fetchDogImg.bind(this);

    this.state = {
      dog: '',
    };
  }

  componentDidMount() {
    this.fetchDogImg();
  }

  async fetchDogImg() {
    const myFetch = await fetch("https://dog.ceo/api/breeds/image/random")
    const myResponse = await myFetch.json();
    this.setState({ dog: myResponse });
  }

  render() {
    if (this.state.dog === "") return "loading...";
    return (
      <div>
        <p>Doguinhos</p>
        <button onClick={this.fetchDogImg}>Novo doguinho!</button>
        <div>
          <img src={this.state.dog.message} />
        </div>
      </div>
    );
  }
}

export default App;
